var util = {

    /*** INITIALIZATION ***/

    publicAppInitialized: false,

    appInitialized: false,

    onPublicAppInit: () => {
        /* Routines that should run on public app initialization
        */
        var self = util;

        if(self.publicAppInitialized)
            return;

        self.publicAppInitialized = true;
    },

    onAppInit: () => {
        /* Routines that should run on internal app initialization
        */
        var self = util;

        if(self.appInitialized)
            return;

        self.appInitialized = true;
    },

    /*** NOTIFICATIONS ***/

    msg: (options) => {
        /* Notify the user with a custom message and styling

        :param options: (json) arguments for the UIkit notify method:
            :key message: (str) notification message
            :key status: (str) whether it's a success, error, warning or info
            :key timeout: (int) how long to keep the notification visible (in
                miliseconds)
            :key pos: (str) where the notification should be positioned
        */
        console.log('Message to the user:')
        console.log(options);
    },

    pleaseWait: () => {
        /* Show notification bar in the UI with "Please wait..." message
        */
        console.log('Please wait...');
    },

    loaderBar: (show=true, timeout=0, $loaderBar=undefined) => {
        /* Show animated loading bar in the UI

        :show: (bool) whether to show or hide the message
        :timeout: (int| def: 0) time to wait before showing/hiding the loader
        :$loaderBar: (jQuery| def: appView.$loaderBar) loader bar element to use
        */
        if($loaderBar == undefined) {
            if(!appView || !appView.$loaderBar)
                return false;

            $loaderBar = appView.$loaderBar;
        }

        if(show) {
            setTimeout(function() {
                $loaderBar.show();
            }, timeout);
        } else {
            setTimeout(function() {
                $loaderBar.hide();
            }, timeout);
        }
    },

    closeAllMsg: () => {
        /* Close all notification messages on the UI
        */
        var self = util;
    },

    clearAllNotifications: () => {
        /* Close all messages, loader bars, etc in the UI
        */
        var self = util;

        self.closeAllMsg();
    },


    /*** STRING UTILITIES ***/
    capitalizeFirst: (str) => {
        /* Capitalizes first letter of a string

        :str: (str) the string to capitalize first letter
        */
        return str.charAt(0).toUpperCase() + str.slice(1);
    },


    /*** ARRAY UTILITIES ***/
    arrayShuffle(array) {
        /* Shuffle elements of an array in random order

        :param array: (array) array to shuffle
        */
        for (let i = array.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [array[i], array[j]] = [array[j], array[i]];
        }
        return array;
    },

    /*** MISCELANEOUS ***/

    checkFormFields: (data, fields) => {
        /* Check for errors in form fields submitted

        :data: (json) data submitted by the user
        :fields: (json) instructions from const FORM_VALIDATE to check errors
        */
        var self = util;

        var getErrorMsg = (errorMsg, msgType) => {
            if(typeof errorMsg != 'object')
                return null;

            else if(errorMsg.hasOwnProperty(msgType))
                return errorMsg[msgType];

            else if(errorMsg.hasOwnProperty('default'))
                return errorMsg.default;

            else
                return undefined;
        }

        var checked = {
            valid: true,
            errors: []
        };

        if(typeof data != 'object')
            data = {};

        Object.keys(fields).forEach((field) => {
            if((!data.hasOwnProperty(field) || data[field].length == 0) && 
                fields[field].mandatory) {
                checked.valid = false;
                checked.errors.push({
                    field: field,
                    msg: getErrorMsg(fields[field].errorMsg, 'mandatory')
                });
            }

            var value = data[field];

            if(fields[field].type == 'strNum') {
                if(isNaN(value)) {
                    checked.valid = false;
                    checked.errors.push({
                        field: field,
                        msg: getErrorMsg(fields[field].errorMsg, 'type')
                    });
                }

            } else {
                if(typeof value !== fields[field].type) {
                    checked.valid = false;
                    checked.errors.push({
                        field: field,
                        msg: getErrorMsg(fields[field].errorMsg, 'type')
                    });
                }
            }

        });

        return checked;
    },

    getUserFromToken: (token) => {
        /* Extract encoded user data from authentication token string
        */
        var tokenParts = token.split('.');

        var header = JSON.parse(window.atob(tokenParts[0]));
        var payload = JSON.parse(window.atob(tokenParts[1]));
        var signature = tokenParts[2];

        var user = payload.public.user;

        return user;
    },

    replaceAll: (args) => {
        /* Search and replace multiple occurrencies of a value in a string

        :arg source: (str) source string search on
        :arg search: (str) string to search for
        :arg replace: (str) value to replace with
        */
        var self = util;

        var source = args.source;
        return args.source.split(args.search).join(args.replace);
    },

    fillTemplate: (args) => {
        /* Takes a string and replace string placeholders with values

        :arg template: (string) the base string with placeholders that
            should bereplaced by specified values
        :arg data: (object) a series of placeholder:value pairs to be
            used in the string replacement
        :return: (string) the template with placeholders replaced by the
            respective values
        */
        var self = util;

        for(key in args.data) {
            args.template = self.replaceAll({
                source: args.template,
                search: `{{${key}}}`,
                replace: args.data[key]
            });
        }

        return args.template;
    },

};

var gaEvent = function(event) {
    if(typeof ga === 'undefined')
        return;

    if(!event)
        return gaEventError({
            event: event,
            field: 'event',
            format: 'json object',
        });

    if(typeof event.category != 'string' || typeof event.action != 'string')
        return gaEventError({
            event: event,
            field: 'category and action',
            format: 'string',
        });

    if(event.label !== undefined && typeof event.label != 'string')
        return gaEventError({
            event: event,
            field: 'label',
            format: 'string',
        });

    if(event.value !== undefined && Number.isInteger(event.value))
        return gaEventError({
            event: event,
            field: 'value',
            format: 'integer',
        });

    var fieldsObject = {
        hitType: 'event',
        eventCategory: event.category,
        eventAction: event.action,
    };

    if(event.label)
        fieldsObject['eventLabel'] = event.label;

    if(event.value)
        fieldsObject['eventValue'] = event.value;

    var result = ga('send', fieldsObject);
}


var gaEventError = function(args) {
    console.error('Google Analytics event must provide '+ args.field +' in '+ 
        args.format +' format.');
    console.error(args.event);
    return false;
}


var gaEventFromElement = function($elem) {
    gaEvent({
        category: $elem.attr('ga-category'),
        action: $elem.attr('ga-action'),
        label: $elem.attr('ga-label'),
        value: $elem.attr('ga-value'),
    });
}


